import esriId from "@arcgis/core/identity/IdentityManager"

// Use `session.toCredential()` to conver the ArcGIS REST auth into a format suitable to pass into the JS API `registerToken` function. This sends the auth from ArcGIS REST JS to the JS API.
export function syncAuthToJsApi() { esriId.registerToken(this.session.toCredential()) }

 // Update the display of the current auth manager by converting it to JSON.
export function updateDisplay() { document.getElementById("sessionInfo").textContent = JSON.stringify(this.session.toJSON(), null, 2) }
