export async function idManager(path) { console.log(path)
  return await fetch(`http://localhost:9000/api/auth/${path}`, {method: "GET",})
  .then((response) => { // If request is not successful, display error message
    if (!response.ok) { throw new Error("HTTP status " + response.status) }
    return response.json()
  })
  .catch((err) => { console.log(err) })
}

export async function searchItems({ keyword }) {
  return await fetch(`/api/homepage/getItems/${keyword}`, {
    method: "GET",
    headers: { "Content-Type": "application/json" },
  })
  .then((response) => { // If request is not successful, display error message
    if (!response.ok) { throw new Error("HTTP status " + response.status) } 
    return response.json()
  })
  .catch((err) => { console.log(err) })
}
