import {
 SELECTION_SET,
 SELECTION_ADD,
 SELECTION_REMOVE,
} from '../../../constants/action-types';

export const INITIAL_STATE = null;

const reducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case SELECTION_SET:
    case SELECTION_ADD:
    case SELECTION_REMOVE:
      return action.layer || INITIAL_STATE;
    default:
      break;
  }
  return state;
};

export default reducer;
