import esriConfig from '@arcgis/core/config';
import OAuthInfo from '@arcgis/core/identity/OAuthInfo';
import IdentityManager from '@arcgis/core/identity/IdentityManager';
/* eslint-enable */

import { APP_ID, APP_PORTAL_URL } from '../constants/app-constants'

import {
  GET_IDENTITY,
  SIGN_IN,
  SIGN_OUT,
  LOAD_PORTAL,
} from '../constants/action-types'

esriConfig.portalUrl = APP_PORTAL_URL;
const info = new OAuthInfo({ appId: APP_ID, popup: false, portalUrl: APP_PORTAL_URL })
IdentityManager.registerOAuthInfos([info])

/*
const response = await fetch('/server/api/authentication', {
  method: 'POST',
  headers: { 'Content-Type': 'application/json', },
  body: JSON.stringify({ post: action.type }),
})*/

/**
 * Middleware function with the signature
 *
 * storeInstance =>
 * functionToCallWithAnActionThatWillSendItToTheNextMiddleware =>
 * actionThatDispatchWasCalledWith =>
 * valueToUseAsTheReturnValueOfTheDispatchCall
 *
 * Typically written as
 *
 * store => next => action => result
 */
//https://www.arcgis.com/sharing/oauth2/authorize?client_id=DFKBboJXHtIuzvHj&response_type=code&state=%7B%22portalUrl%22%3A%22https%3A%2F%2Fwww.arcgis.com%22%2C%22uid%22%3A%22Y3_UDNr5mlf0c0YO0RfKG8u-6B9VNk4P0_5E6t1v-30%22%7D&expiration=20160&redirect_uri=http%3A%2F%2Flocalhost%3A9000%2F&code_challenge=X29y-S7fqMGxWnelqqxSsTAZNMm3q5A1BYbyKz-7PRc&code_challenge_method=S256
const arcgisMiddleWare = store => next => (action) => {
  switch (action.type) {
    case GET_IDENTITY: {
      next(action)
      const credential = IdentityManager.checkSignInStatus(`${info.portalUrl}/sharing`)
      console.log(credential)
      return credential
        .then((result) => { //console.log(result)
          store.dispatch({ type: LOAD_PORTAL })
        })
    }
    case SIGN_IN: {
      IdentityManager.getCredential(`${info.portalUrl}/sharing`)
      return next(action)
    }
    case SIGN_OUT: {
      IdentityManager.destroyCredentials()
      window.location.reload()
      return next(action)
    }
    default: {
      return next(action)
    }
  }
}

export default arcgisMiddleWare
