import React from 'react'
import { connect } from 'react-redux'
import "react-tabulator/lib/styles.css" // default theme
import "react-tabulator/css/tabulator_modern.min.css" // use Theme(s)
import { ReactTabulator } from 'react-tabulator'
import DateEditor from "react-tabulator/lib/editors/DateEditor"
import MultiValueFormatter from "react-tabulator/lib/formatters/MultiValueFormatter"
import MultiSelectEditor from "react-tabulator/lib/editors/MultiSelectEditor"

interface Item {
  id: string,
  title?: string,
  created?: Date,
  tags?: string[],
}

interface Tags {
  tag: string,
  count: number,
}

interface ItemProps {
  userItems: Item[],
  userTags?: Tags[],
}

const petOptions = [
  { name: "cat" },
  { name: "dog" },
  { name: "fish" }
]

export class QueryTableTabulator extends React.Component <ItemProps, any> {
  public static defaultProps = { userItems: [{id:'',}], }

  tableColumns: any[] = [ //ColumnDefinition[] = [
    {formatter:"rowSelection", titleFormatter:"rowSelection", headerSort:false},
    { title: "Title", field: "title", },
    { title: "Created", field: "created", },
    { title: "Tags", field: "tags", 
      editor: MultiSelectEditor,
        editorParams: { values: petOptions},//this.props.userTags?.map(({tag}) => tag )},
        formatter: MultiValueFormatter, //React warning
        formatterParams: { style: "PILL" }
    },
  ]

  tableOptions = {
    layout: "fitDataStretch",
    movableRows: true,
    movableColumns: true,
    pagination: true,
      paginationSize: 5,
      paginationInitialPage: 1,
      paginationSizeSelector: [5,10,20,50,100],
  }

  render() { console.log("query-table-tabulator")
    return (
      <ReactTabulator
        data={this.props.userItems}
        columns={this.tableColumns}
        options={this.tableOptions}
      />
    )
  }
}

const mapStateToProps = ({ query: { userItems, userTags }}:any) => ({
  userItems,
  userTags
})

export default connect(mapStateToProps, )(QueryTableTabulator)
