import React from 'react'
import { connect } from 'react-redux'
import * as qActions from '../../reducer/portal/query/actions'

interface ViewProps {
  submitForm: Function,
}

export class Query extends React.Component <ViewProps, any> {
  render() { console.log("query")
    return (
      <div className="queryForm">
        <form method="GET" id="qForm" onSubmit={() => this.props.submitForm()}>
          <label>Title<input type="text" id="qTitle" name="title" placeholder="Title" /></label>
          <fieldset className="radio-group">
            <legend className="radio-group-title">Search before or after selected date:</legend>
            <input className="radio-group-input" type="radio" id="qRCreatedBefore" name="rcreated" value="before" checked/>
            <label className="radio-group-label" htmlFor="qRCreatedBefore">Before</label>
            <input className="radio-group-input" type="radio" id="qRCreatedAfter" value="after" name="rcreated"/>
            <label className="radio-group-label" htmlFor="qRCreatedAfter">After</label>
          </fieldset>
          <input type="date" id="qDCreated" name="dcreated" placeholder="mm/dd/yyyy" />
            <label><input type="radio" id="qRCreatedBefore" name="rcreated" value="before" defaultChecked/>Before</label>
            <label><input type="radio" id="qRCreatedAfter" name="rcreated" value="after" />After</label>
          <input type="submit" value="Query" onSubmit={() => this.props.submitForm()} />
          <input type="hidden" id="qCreated" name="created" />
        </form>
      </div>
    )
  }
}

const mapStateToProps = ({ query: { userItems }}:any) => ({
  userItems,
})
const mapDispatchToProps = (dispatch:any) => ({
  submitForm() { 
    return () => {
      dispatch(qActions.getUserItems)
    }
  }
})

export default connect(mapStateToProps, mapDispatchToProps)(Query)
