    return (
      <div className="queryForm">
         <form method="GET" onSubmit={this.props.getUserItems()}>
            <div>
              <label htmlFor="qTitle">Title</label>
              <input type="text" id="qTitle" name="title" placeholder="Title"></input>
            </div>
            <div>
              <label htmlFor="qType">Type</label>
              <input type="text" id="qType" name="type" placeholder="Type"></input>
            </div>
            <div>
              <label htmlFor="qUCreated">Created (mm/dd/yyyy)</label>
              <input type="text" id="qUCreated" name="ucreated" placeholder="Created"></input>
            </div>
            <div>
              <input type="submit" value="Query" onSubmit={this.props.getUserItems()}></input>
            </div>
            <div style={{display:'none'}}>
              <input type="hidden" id="qCreated" name="created"></input>
            </div>
          </form>
      </div>
    )
  }
}

const mapStateToProps = ({ query: { userItems }}:any) => ({
  userItems,
})
const mapDispatchToProps = (dispatch:any) => ({
  getUserItems() {
    console.log("getting user items")
    return () => {
      dispatch(qActions.getUserItems)
    }
  }
})

export default connect(mapStateToProps, mapDispatchToProps)(Query)
