import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

export class ItemNav extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      mouse: false
    }
  }

  mouseEnter() {
    this.setState({
      mouse: true,
    })
  }

  mouseLeave() {
    this.setState({
      mouse: false,
    })
  }

  toggleMenu() {
    this.setState({
      open: !this.state.open,
    });
  }

  collapseMenu() {
    if (this.state.mouse) return;
    this.setState({ open: false });
  }

  render() {
    return (
      <div
        className={`dropdown ${this.state.open ? ' is-active' : ''}`}
        onMouseEnter={() => this.mouseEnter()}
        onBlur={() => this.collapseMenu()}
        onMouseLeave={() => this.mouseLeave()}
      >

        <button className="top-nav-title dropdown-btn" onMouseDown={() => this.toggleMenu()}>
          ArcGIS Item Management &nbsp;
          <i className="icon-ui-down-arrow" />
        </button>
        <nav className="dropdown-menu modifier-class">
          <span className="dropdown-title">
            {this.props.username ?
              <em><a
              href={`/?username=${this.props.username}`}
              className="dropdown-link"
                >Load Items.
              </a></em> :
              <em>You are not logged in.</em>}
            <br />
          </span>
        </nav>
      </div>
    )
  }
}

ItemNav.propTypes = {
  username: PropTypes.string,
  websceneItems: PropTypes.array
}

ItemNav.defaultProps = {
  username: null,
  websceneItems: [],
};

const mapStateToProps = ({ user: { username, websceneItems } }) => ({
  username,
  websceneItems,
})

export default connect(mapStateToProps)(ItemNav)
