import React from 'react'
import { connect } from 'react-redux'
import 'react-tabulator/lib/styles.css';
import "react-tabulator/css/bootstrap/tabulator_bootstrap.min.css"; // use Theme(s)
import { ColumnDefinition, ReactTabulator, reactFormatter } from 'react-tabulator'

function SimpleButton(props:any) {
  const rowData = props.cell._cell.row.data;
  const cellValue = props.cell._cell.value || "Edit | Show";
  return <button onClick={() => alert(rowData.name)}>{cellValue}</button>;
}



var data = [
  {id:1, title:"Oli Bob", created:"12", tags:"red", },
]

export class QueryTabulator extends React.Component {
  state = {
    data: [],
    selectedName: ""
  }
  ref = null

  columns: ColumnDefinition[] = [
    { title: "Title", field: "title", },
    { title: "Created", field: "created", },
    { title: "Tags", field: "tags" },
    { title: "Custom", field: "custom", hozAlign: "center",editor: "input",
      formatter: reactFormatter(
          <SimpleButton
            onSelect={(name:any) => {
              this.setState({ selectedName: name });
              alert(name);
            }}
          />
        )
    }
  ]

  rowClick = (e:any, row:any) => {
    // console.log("ref table: ", this.ref.current); // this is the Tabulator table instance
    // console.log("rowClick id: ${row.getData().id}", row, e);
    this.setState({ selectedName: row.getData().name });
  }
  setData = () => { this.setState({ data })}
  clearData = () => { this.setState({ data: [] })}

  render() { console.log("query-tabulator")
    const options = {
      height: 150,
      movableRows: true,
      movableColumns: true
    }
    return (
      <div>
        <ReactTabulator
          onRef={(ref) => (this.ref = ref)}
          columns={this.columns}
          data={data}
          events={{
            rowClick: this.rowClick
          }}
          options={options}
          data-custom-attr="test-custom-attribute"
          className="custom-css-class"
        />
        <i>
          Selected Name: <strong>{this.state.selectedName}</strong>
        </i>

        <h3>
          Asynchronous data: (e.g. fetch) -{" "}
          <button onClick={this.setData}>Set Data</button>
          <button onClick={this.clearData}>Clear</button>
        </h3>
        <ReactTabulator columns={this.columns} data={this.state.data} />
      </div>
    )
  }
}

const mapStateToProps = ({ query: { userItems }}:any) => ({
  userItems,
})

export default connect(mapStateToProps, )(QueryTabulator)
