 * limitations under the License.
 *
 */
/*eslint-disable */
import Portal from '@arcgis/core/portal/Portal'
/* eslint-enable */

import {
  LOAD_PORTAL,
  SET_IDENTITY,
  GET_USER_ITEMS,
  SET_USER_ITEMS,
} from '../constants/action-types'

const portal = new Portal({ authMode: 'immediate' })
function getUrlParamArray(param)
{
  param = param.replace(/([\[\](){}*?+^$.\\|])/g, "\\$1");
  var value = [];
  var regex = new RegExp("[?&]" + param + "=([^&#]*)", "g");
  var url   = decodeURIComponent(window.location.href);
  var match = null;
  while (match = regex.exec(url)) {
    value.push(match[1]);
  }    
  return value;
}

/**
 * Middleware function with the signature
 *
 * storeInstance =>
 * functionToCallWithAnActionThatWillSendItToTheNextMiddleware =>
 * actionThatDispatchWasCalledWith =>
 * valueToUseAsTheReturnValueOfTheDispatchCall
 *
 * Typically written as
 *
 * store => next => action => result
 */
const arcgisMiddleWare = store => next => (action) => {
  switch (action.type) {
    case LOAD_PORTAL:
      portal.load()
      .then(() => {
        store.dispatch({
          type: SET_IDENTITY,
          username: portal.user.username,
          fullname: portal.user.fullName,
          email: portal.user.email,
          thumbnailurl: portal.user.thumbnailUrl,
        })
        store.dispatch({ type: GET_USER_ITEMS })
      })

    case GET_USER_ITEMS:
      next(action)
      let qParam = getUrlParamArray("qTitle")
      console.log(portal.user)
      console.log(qParam[0])
      try { 
        return portal.queryItems({
          query: `owner:${portal.user.username}`,
          sortField: 'modified',
          sortOrder: 'desc',
          num: 15,
        })
        .then(({ results }) => store.dispatch({
          type: SET_USER_ITEMS,
          userItems: results,
        }))
      }
      catch { return 0 }
      finally { return 0 }
      
    default:
      return next(action)
  }
}

export default arcgisMiddleWare
