import React from 'react'
import { connect } from 'react-redux'
import { DEFAULT_SCENE_ID } from '../../constants/app-constants'

interface SceneProps {
  username: string,
  websceneItems: []
}

export class ScenesNav extends React.Component <SceneProps, { open: boolean, mouse: boolean }> {
  constructor(props: any) {
    super(props);
    this.state = {
      open: false,
      mouse: false,
    }
  }

  public static defaultProps = {
    username: '',
    websceneItems: [],
  }

  mouseEnter() { this.setState({ mouse: true, }) }
  mouseLeave() { this.setState({ mouse: false, }) }
  toggleMenu() { this.setState({ open: !this.state.open, }) }
  collapseMenu() { if (this.state.mouse) return; this.setState({ open: false }) }

  render() {
    return (
      <div
        className={`dropdown ${this.state.open ? ' is-active' : ''}`}
        onBlur={() => this.collapseMenu()}
        onMouseEnter={() => this.mouseEnter()}
        onMouseLeave={() => this.mouseLeave()}
      >
        <button className="top-nav-link dropdown-btn" onMouseDown={() => this.toggleMenu()}>
          Scenes &nbsp;
          <i className="icon-ui-down-arrow" />
        </button>
        <nav className="dropdown-menu modifier-class">
          <span className={`dropdown-title ${this.props.websceneItems.length ? 'hidden' : ''}`}>
            {this.props.username ?
              <em>You have no web scenes.</em> :
              <em>You are not logged in.</em>}
            <br />
            <a href={`/?id=${DEFAULT_SCENE_ID}`}>Open a default scene?</a>
          </span>
        </nav>
      </div>
    )
  }
}

const mapStateToProps = ({ user: { username, websceneItems } }:any) => ({
  username,
  websceneItems,
})

export default connect<SceneProps, any>(mapStateToProps)(ScenesNav as any)
