import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

export class ItemNav extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mouse: false
    }
  }

  mouseEnter() {
    this.setState({
      mouse: true,
    })
  }

  mouseLeave() {
    this.setState({
      mouse: false,
    })
  }

  render() {
    return (
      <div
        className={`dropdown ${this.state.open ? ' is-active' : ''}`}
        onMouseEnter={() => this.mouseEnter()}
        onMouseLeave={() => this.mouseLeave()}
      >
        <button className="top-nav-link dropdown-btn" >
          Query &nbsp;
          <i className="icon-ui-down-arrow" />
        </button>
        <nav className="dropdown-menu modifier-class">
          <span className={`dropdown-title ${this.props.websceneItems.length ? 'hidden' : ''}`}>
            {this.props.username ?
              <em>You have no web scenes.</em> :
              <em>You are not logged in.</em>}
            <br />
            <a href={`/?id=${DEFAULT_SCENE_ID}`}>Open a default scene?</a>
          </span>
          {this.props.websceneItems.map(item => (
            <a
              href={`/?id=${item.id}`}
              className="dropdown-link"
              key={item.id}
            >
              {item.title}
            </a>
          ))}
        </nav>
      </div>
    )
  }
}

ScenesNav.propTypes = {
  username: PropTypes.string
}

ScenesNav.defaultProps = {
  username: null
};

const mapStateToProps = ({ user: {username} }) => ({ username })

export default connect(mapStateToProps)(ItemNav);
