import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

export class ItemNav extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      mouse: false
    }
  }

  mouseEnter() {
    this.setState({
      mouse: true,
    })
  }

  mouseLeave() {
    this.setState({
      mouse: false,
    })
  }

  toggleMenu() {
    this.setState({
      open: !this.state.open,
    });
  }

  collapseMenu() {
    if (this.state.mouse) return;
    this.setState({ open: false });
  }

  render() {
    return (
      <div
        className={`${this.state.open ? ' is-active' : ''}`}
        onMouseEnter={() => this.mouseEnter()}
        onMouseLeave={() => this.mouseLeave()}
      >
        <button className="top-nav-link dropdown-btn" onMouseDown={() => this.toggleMenu()}>
          Start &nbsp;
          <i className="icon-ui-down-arrow" />
        </button>
        <nav className="dropdown-menu modifier-class">
          <span className={`dropdown-title`}>
            {this.props.username ?
              <em>You have no items.</em> :
              <em>You are not logged in.</em>}
            <br />
          </span>
            <a
              href={`/?username=${this.props.username}`}
              className="dropdown-link"
              key={this.props.username}
            >
              Load Items
            </a>
        </nav>
      </div>
    )
  }
}

ItemNav.propTypes = {
  username: PropTypes.string
}

ItemNav.defaultProps = {
  username: null
};

const mapStateToProps = ({ user: {username} }) => ({ username })

export default connect(mapStateToProps)(ItemNav);
