import React from 'react'
import { connect } from 'react-redux'
//import 'react-tabulator/lib/styles.css';
import "react-tabulator/css/bootstrap/tabulator_bootstrap4.min.css"; // use Theme(s)
import { ColumnDefinition, ReactTabulator } from 'react-tabulator'

interface Item {
  id: string,
  title?: string,
  created?: Date,
  tags?: string[],
}

interface Tags {
  tag: string,
  count: number,
}

interface ItemProps {
  userItems: Item[],
  userTags?: Tags[],
}

export class QueryTableTabulator extends React.Component <ItemProps, any> {
  constructor(props: any) {
    super(props)
    this.state = {

    }
  }

  ref = null
  public static defaultProps = { userItems: [{id:'',}], }

  columns: any[] = [ //ColumnDefinition[] = [
    {rowHandle:true, formatter:"handle", headerSort:false, frozen:true, width:30, minWidth:30},
    { title: "Title", field: "title", },
    { title: "Created", field: "created", },
    { title: "Tags", field: "tags", 
      editor: true,
        editorParams: { values: {"Tag12":"Tag12","Tag123":"Tag123"}},//this.props.userTags?.map(({tag}) => tag )},
      formatterParams: { style: "PILL" }

    },
  ]

  setData = () => { this.setState( this.props.userItems )}
  clearData = () => { this.setState( [] )}

  render() { console.log("query-table-tabulator")
    const options = {
      //selectable: true,
      movableRows: true,
      movableColumns: true,
      layout: "fitDataTable",
      pagination: "local",
        paginationSize: 5,
        paginationInitialPage: 1,
        paginationSizeSelector: [5,10,20,50,100],
    }

    return (
      <div>
        <ReactTabulator 
          //onRef={(r) => (this.ref = r)}
          columns={this.columns}
          data={this.props.userItems} 
          options={options}          
        />
      </div>
    )
  }
}

const mapStateToProps = ({ query: { userItems, userTags }}:any) => ({
  userItems,
  userTags
})

export default connect(mapStateToProps, )(QueryTableTabulator)
