import React from 'react'
import { connect } from 'react-redux'
import * as qActions from '../../reducer/portal/query/actions'
import * as uActions from '../../reducer/portal/user/actions'
import { autoFormatTitle } from './query-form-helpers'

interface ViewProps {
  portalQueryParam: string,
  getUserItems: Function,
}

export class Query extends React.Component <ViewProps, any> {
  constructor(props: any) {
    super(props);
    this.state = {

    }
  }

  public static defaultProps = {
    portalQueryParam: "",
  }

  render() {
    console.log("query")

    const title = document.getElementById("qTitle")
    if(title != null) { 
      title.oninput = (e:any) => {
        e.target.value = autoFormatTitle(e.target.value)
      }
    }

    return (
      <div className="queryForm">
         <form method="GET">
            <label htmlFor="qTitle">Title:</label>
            <input type="text" id="qTitle" name="title"></input>
            <label htmlFor="qType">Type:</label>
            <input type="text" id="qType" name="type"></input>
            <label htmlFor="qCreated">Created:</label>
            <input type="text" id="qCreated" name="created"></input>
            <input type="submit" value="Query" onClick={this.props.getUserItems()}></input>
          </form>
      </div>
    )
  }
}

const mapStateToProps = ({ query: { userItems }}:any) => ({
  userItems,
})
const mapDispatchToProps = (dispatch:any) => ({
  getUserItems() {
    console.log("getting user items")
    return () => {
      dispatch(qActions.getUserItems)
    }
  }
})

export default connect(mapStateToProps, mapDispatchToProps)(Query)
