import React from 'react'
import { connect } from 'react-redux'
//import 'react-tabulator/lib/styles.css';
import "tabulator-tables/dist/css/tabulator_bootstrap4.min.css"; // use Theme(s)
import { ColumnDefinition, Tabulator, FormatModule, EditModule } from 'tabulator-tables'
Tabulator.registerModule([FormatModule, EditModule])

interface Item {
  id: string,
  title?: string,
  created?: Date,
  tags?: string[],
}

interface Tags {
  tag: string,
  count: number,
}

interface ItemProps {
  userItems: Item[],
  userTags?: Tags[],
}

export class QueryTableTabulator extends React.Component <ItemProps, any> {
  public static defaultProps = { userItems: [{id:'',}], }
  el = React.createRef()
  tabulator:any

  componentDidMount() {
    //instantiate Tabulator when element is mounted
    this.tabulator = new Tabulator(this.el as any, {
      data: this.props.userItems, //link data to table
      reactiveData:true, //enable data reactivity
      columns: this.tableColumns, //define table columns
      movableRows: true,
      movableColumns: true,
      layout: "fitDataTable",
      pagination: true,
        paginationSize: 5,
        paginationInitialPage: 1,
        paginationSizeSelector: [5,10,20,50,100],
    })
  }

  tableColumns: any[] = [ //ColumnDefinition[] = [
    //{rowHandle:true, formatter:"handle", headerSort:false, frozen:true, width:30, minWidth:30},
    { title: "Title", field: "title", },
    { title: "Created", field: "created", },
    { title: "Tags", field: "tags", 
      editor: "list",
        editorParams: { values: this.props.userTags?.map(({tag}) => tag )},
      formatterParams: { style: "PILL" }
    },
  ]

  setData = () => { this.setState( this.props.userItems )}
  clearData = () => { this.setState( [] )}

  render() { console.log("query-table-tabulator")
    return (
      <div ref={el => (this.el = el as any)} />
    )
  }
}

const mapStateToProps = ({ query: { userItems, userTags }}:any) => ({
  userItems,
  userTags
})

export default connect(mapStateToProps, )(QueryTableTabulator)
