import React from 'react'
import { connect } from 'react-redux'
import * as qActions from '../../reducer/portal/query/actions'

interface ViewProps {
  getUserItems: Function,
}

export class Query extends React.Component <ViewProps, any> {
  constructor(props: any) {
    super(props);
    this.state = {

    }
  }

  submitForm() {
    console.log("getting user items")

    const myUrl = new URL(window.location.href)
    const params = new URLSearchParams(myUrl.search)
    const qDCreated = params.get("dcreated")

    if (qDCreated) {
      const created = new Date(qDCreated)
      let createdUnix = created.getTime().toString()
      if(params.get("rcreated")=="before") {
        createdUnix = '[0 TO ' + createdUnix + ']'
      } else if (params.get("rcreated")=="after") {
        createdUnix = '[' + createdUnix + ' TO 99999999999999]'
      }
      params.set("created",createdUnix)
      console.log(createdUnix)
    }

    myUrl.search = params.toString()
    const new_url = myUrl.toString(); console.log(new_url)
    window.location.href = new_url
    window.history.replaceState('Object','',myUrl.search)

    console.log('reloading')
    //window.location.reload()

    this.props.getUserItems()
  }

  render() {
    console.log("query")

    return (
      <div className="queryForm">
         <form method="GET" id="qForm" onSubmit={() => this.submitForm()}>
            <div>
              <label htmlFor="qTitle">Title</label>
              <input type="text" id="qTitle" name="title" placeholder="Title"></input>
            </div>
            <div>
              <div>
                <input type="radio" id="qRCreatedBefore" name="rcreated" value="before"></input>
                <label htmlFor="qRCreatedBefore">Before</label>
                <input type="radio" id="qRCreatedAfter" name="rcreated" value="after"></input>
                <label htmlFor="qRCreatedAfter">After</label>
              </div>
              <label htmlFor="qDCreated">Created</label>
              <input type="date" id="qDCreated" name="dcreated" 
              placeholder="mm/dd/yyyy"></input>
            </div>
            <div>
              <input type="submit" value="Query" onSubmit={() => this.submitForm()}></input>
            </div>
            <div>
              <input type="hidden" id="qCreated" name="created"></input>
            </div>
          </form>
      </div>
    )
  }
}

const mapStateToProps = ({ query: { userItems }}:any) => ({
  userItems,
})
const mapDispatchToProps = (dispatch:any) => ({
  getUserItems() {
    return () => {
      dispatch(qActions.getUserItems)
    }
  }
})

export default connect(mapStateToProps, mapDispatchToProps)(Query)
