 */

import itemReducer from './item';

import {
  SELECTION_SET,
} from '../../constants/action-types';

const initialState = [];

export default (state = initialState, action) => {
  switch (action.type) {
    case SELECTION_SET:
      return [itemReducer({}, action)]
    default:
      return state
  }
}
