import React from 'react'
import { connect } from 'react-redux'

interface SceneProps {
  username: string,
  websceneItems: []
}

export class QueryNav extends React.Component <SceneProps, { open: boolean, mouse: boolean }> {
  constructor(props: any) {
    super(props);
    this.state = {
      open: false,
      mouse: false,
    }
  }

  public static defaultProps = {
    username: '',
    websceneItems: [],
  }

  mouseEnter() { this.setState({ mouse: true, }) }
  mouseLeave() { this.setState({ mouse: false, }) }

  render() {
    return (
      <div
        className={`dropdown ${this.state.open ? ' is-active' : ''}`}
        onMouseEnter={() => this.mouseEnter()}
        onMouseLeave={() => this.mouseLeave()}
      >
        <button className="top-nav-link">
          Query &nbsp;
        </button>
      </div>
    )
  }
}

const mapStateToProps = ({ user: { username, websceneItems } }:any) => ({
  username,
  websceneItems,
})

export default connect<SceneProps, any>(mapStateToProps)(ScenesNav as any)
