 * limitations under the License.
 *
 */
/*eslint-disable */
import Portal from '@arcgis/core/portal/Portal'
/* eslint-enable */

import {
  LOAD_PORTAL,
  SET_IDENTITY,
  GET_USER_ITEMS,
  SET_USER_ITEMS,
} from '../constants/action-types'

const portal = new Portal({ authMode: 'immediate' })

const qParamArray = {searchFields:["title","type"], getFields:[], qString: ''}

/**
 * Middleware function with the signature
 *
 * storeInstance =>
 * functionToCallWithAnActionThatWillSendItToTheNextMiddleware =>
 * actionThatDispatchWasCalledWith =>
 * valueToUseAsTheReturnValueOfTheDispatchCall
 *
 * Typically written as
 *
 * store => next => action => result
 */
const arcgisMiddleWare = store => next => (action) => {
  switch (action.type) {
    case LOAD_PORTAL:
      portal.load()
      .then(() => {
        store.dispatch({
          type: SET_IDENTITY,
          username: portal.user.username,
          fullname: portal.user.fullName,
          email: portal.user.email,
          thumbnailurl: portal.user.thumbnailUrl,
        })
        store.dispatch({ type: GET_USER_ITEMS })
      })

    case GET_USER_ITEMS:
      next(action)

      let qString = ''
      qParamArray.searchFields.forEach(
        field => {
          console.log(field)
          console.log(getUrlParamArray(field))
          if(getUrlParamArray(field) != undefined){
            qParamArray.getFields[field] = ' AND ' + field + ':' + getUrlParamArray(field)
          }
          console.log(qParamArray.getFields[field])
          qString += qParamArray.getFields[field]
        }
      )
      
      console.log(qString)
      console.log(portal.user.username)
      try { 
        return portal.queryItems({
          query: `owner:${portal.user.username}`,
          sortField: 'modified',
          sortOrder: 'desc',
          num: 15,
        })
        .then(({ results }) => store.dispatch({
          type: SET_USER_ITEMS,
          userItems: results,
        }))
      }
      catch { return 0 }
      finally { return 0 }
      
    default:
      return next(action)
  }
}

export default arcgisMiddleWare
