import React from 'react'
import { connect } from 'react-redux'
import PortalItem from '@arcgis/core/portal/PortalItem'

interface Item {
  id: string,
  title?: string,
  created?: Date,
  tags?: string[]
}

interface ItemProps {
  userItems: Item[],
  userTags: string[],
}

export class QueryTable extends React.Component <ItemProps, any> {
  public static defaultProps = { userItems: [{id:'',}], userTags: ['',]}

  addTags() { console.log("adding tags")
    const selectCollection = document.querySelectorAll("select.selectTags option:checked"); console.log(selectCollection)
    const selectCollection2 = document.querySelectorAll("select.selectTags option:checked"); console.log(selectCollection2)
    selectCollection.forEach(select => console.log(select.id.replace('option','')))
    selectCollection2.forEach(select => console.log(select.nodeValue?.replace('option','')))

    let portalItem = new PortalItem()

  }

  render() { console.log("component: query-table")
    return (
        <table className="center">
          <thead>
            <tr>
              <th>title</th><th>created</th><th>tags</th>
              <th><button className="addTagButton" onClick={() => this.addTags()} >add tag(s)</button></th>
            </tr>
          </thead>
          <tbody>
            {this.props.userItems.map((userItems,index) => (
            <tr key={index} id={userItems.id}>
              <td>{userItems.title}</td>
              <td>{userItems.created!.toDateString()}, Unix:{userItems.created!.getTime().toString()}</td> 
              <td> 
                {userItems.tags!.map((tag,index) => (
                  <button className="tagButton" key={index} disabled>{tag}</button>
                ))}
              </td>
              <td>
                <select className="selectTags" name={'select'+userItems.id} id={'select'+userItems.id}>
                  <option key="" value=""></option>
                  {(this.props.userTags!
                    .filter(e => !userItems.tags!.includes(e)))
                    .map((tag) => (
                      <option key={tag} value={tag} id={'option'+tag+userItems.id}>{tag}</option> //depends on tag sanitization   
                    )
                  )}
                </select>
              </td>
            </tr>
            ))}
          </tbody>
        </table>
    )
  }
}

const mapStateToProps = ({ query: { userItems, userTags }}:any) => ({
  userItems,
  userTags,
})

export default connect(mapStateToProps, )(QueryTable)
