import React from 'react'
import { connect } from 'react-redux'
import 'react-tabulator/lib/styles.css';
import "react-tabulator/css/bootstrap/tabulator_bootstrap.min.css"; // use Theme(s)
import { ColumnDefinition, ReactTabulator } from 'react-tabulator'

interface Item {
  id: string,
  title?: string,
  created?: Date,
  tags?: string[],
}

interface Tags {
  tag: string,
  count: number,
}

interface ItemProps {
  userItems: Item[],
  userTags?: Tags[],
}

export class QueryTableTabulator extends React.Component <ItemProps, any> {
  constructor(props: any) {
    super(props)
    this.state = {
      selectedTitle: ""
    }
  }

  ref = null
  public static defaultProps = { userItems: [{id:'',}], userTags: []}

  columns: ColumnDefinition[] = [
    { title: "Title", field: "title", },
    { title: "Created", field: "created", },
    { title: "Tags", field: "tags" },
  ]

  rowClick = (e:any, row:any) => { this.setState({ selectedTitle: row.getData().title }) }
  setData = () => { this.setState( this.props.userItems )}
  clearData = () => { this.setState( [] )}

  render() { console.log("query-table-tabulator")
    const options = {
      height: 150,
      movableRows: true,
      movableColumns: true,
      progressiveLoad: 'scroll',
      progressiveLoadDelay: 200,
      selectable: true,
      rowClick: (e:any, row:any) => { this.setState({ selectedTitle: row.getData().title }) }
    }

    return (
      <div>
        <ReactTabulator 
          onRef={(r) => (this.ref = r)}
          columns={this.columns}
          data={this.props.userItems} 
          events={{ rowClick: this.rowClick }}
        />
      </div>
    )
  }
}

const mapStateToProps = ({ query: { userItems, userTags }}:any) => ({
  userItems,
})

export default connect(mapStateToProps, )(QueryTableTabulator)
