import React from 'react'
import { connect } from 'react-redux'
import 'react-tabulator/lib/styles.css';
import { ReactTabulator } from 'react-tabulator'

interface Item {
  id: string,
  title?: string,
  created?: Date,
  tags?: string[]
}

interface ItemProps {
  userItems: Item[],
}

export class QueryTable extends React.Component <ItemProps, any> {
  constructor(props: any) {
    super(props)
    this.state = {

    }
  }

  public static defaultProps = {
    userItems: [{id:'',}],
  }

  render() { console.log("query-table")
    return (
      <div className="queryTable" >
        <table>
          <thead>
            <tr>
              <th></th><th>title</th><th>created</th><th>tags</th><th>...</th>
            </tr>
          </thead>
          <tbody>
            {this.props.userItems.map((userItems,index) => (
            <tr key={index} id={userItems.id}>
              <td><button></button></td>
              <td>{userItems.title}</td>
              <td>{userItems.created?.toDateString()}, Unix:{userItems.created?.getTime().toString()}</td> 
              <td> 
                {userItems.tags?.map((tag,index) => (
                  <span key={index}>{tag} </span>
                ))}
              </td>
              <td><button>Edit tags</button></td>
            </tr>
            ))}
          </tbody>
        </table>

        <ReactTabulator
          data={data}
          columns={columns}
          layout={"fitData"}
        />
      </div>
    )
  }
}

const mapStateToProps = ({ query: { userItems }}:any) => ({
  userItems,
})

export default connect(mapStateToProps, )(QueryTable)



