import React from 'react'
import { connect } from 'react-redux'

interface Item {
  id: string,
  title?: string,
  created?: Date,
  tags?: string[]
}

interface ItemProps {
  userItems: Item[],
  userTags: string[],
}

export class QueryTable extends React.Component <ItemProps, any> {
  constructor(props: any) {
    super(props)
    this.state = {

    }
  }

  public static defaultProps = { userItems: [{id:'',}], userTags: ['',]}

  render() { console.log("query-table")
    const itemTags = this.props.userItems?.map(({tags})=>tags); //console.log(arr1)
    const tagList = this.props.userTags; //console.log(arr2)
    let intersection = []
    for (const element in itemTags) { if (itemTags[element]!.length > 0){ //console.log(arr1[element])}}
      intersection = tagList.filter(function(e) {return itemTags[element]!.indexOf(e) > -1})
    }}
      //[...arr1[element] as string[],...arr2].filter(a => !arr2.includes(a))}
    return (
      <form>
        <table className="center">
          <thead>
            <tr>
              <th>title</th><th>created</th><th>tags</th><th><button>add tag(s)</button></th>
            </tr>
          </thead>
          <tbody>
            {this.props.userItems.map((userItems,index) => (
            <tr key={index} id={userItems.id}>
              <td>{userItems.title}</td>
              <td>{userItems.created?.toDateString()}, Unix:{userItems.created?.getTime().toString()}</td> 
              <td> 
                {userItems.tags?.map((tag,index) => (
                  <button className="tagButton" key={index} disabled>{tag}</button>
                ))}
              </td>
              <td>
                <select>
                  <option key="" value=""></option>
                  {this.props.userTags.map((tag) => (
                    <option key={tag} value={tag}>{tag}</option>
                  ))}
                </select>
              </td>
            </tr>
            ))}
          </tbody>
        </table>
      </form>
    )
  }
}

const mapStateToProps = ({ query: { userItems, userTags }}:any) => ({
  userItems,
  userTags,
})

export default connect(mapStateToProps, )(QueryTable)
