import { idManager } from '../api'
import {
  GET_IDENTITY,
  SIGN_IN,
  SIGN_OUT,
  LOAD_PORTAL,
} from '../constants/action-types'

/**
 * Middleware function with the signature
 *
 * storeInstance =>
 * functionToCallWithAnActionThatWillSendItToTheNextMiddleware =>
 * actionThatDispatchWasCalledWith =>
 * valueToUseAsTheReturnValueOfTheDispatchCall
 *
 * Typically written as
 *
 * store => next => action => result
 */
const arcgisMiddleWare = store => next => (action) => {
  switch (action.type) {
    case GET_IDENTITY: {
      next(action)
      return idManager('checkSignInStatus')
      .then((result) => { //console.log(result)
        store.dispatch({ type: LOAD_PORTAL })
      })
    }
    case SIGN_IN: {
      idManager('signIn')

      return next(action)
    }
    case SIGN_OUT: {
      idManager('signOut')
      window.location.reload()
      return next(action)
    }
    default: {
      return next(action)
    }
  }
}

export default arcgisMiddleWare
