import React from 'react'
import { connect } from 'react-redux'
import 'react-tabulator/lib/styles.css';
import "react-tabulator/css/bootstrap/tabulator_bootstrap.min.css"; // use Theme(s)
import { ColumnDefinition, ReactTabulator, reactFormatter } from 'react-tabulator'

function SimpleButton(props:any) {
  const rowData = props.cell._cell.row.data;
  const cellValue = props.cell._cell.value || "Edit | Show";
  return <button onClick={() => alert(rowData.name)}>{cellValue}</button>;
}

var data = [
  {id:1, title:"Oli Bob", created:"12", tags:"red", },
]

interface Item {
  id: string,
  title?: string,
  created?: Date,
  tags?: string[]
}

interface ItemProps {
  userItems: Item[],
}

export class QueryTableTabulator extends React.Component <ItemProps, any> {
  state = {
    data: [],
    selectedTitle: ""
  }
  ref = null
  public static defaultProps = { userItems: [{id:'',}], }

  columns: ColumnDefinition[] = [
    { title: "Title", field: "title", },
    { title: "Created", field: "created", },
    { title: "Tags", field: "tags" },
  ]

  setData = () => { this.setState({ data })}
  clearData = () => { this.setState({ data: [] })}
  rowClick = (e:any, row:any) => {
    // console.log("ref table: ", this.ref.current); // this is the Tabulator table instance
    // console.log("rowClick id: ${row.getData().id}", row, e);
    this.setState({ selectedTitle: row.getData().title });
  }

  render() { console.log("query-table-tabulator")
    const data = this.props.userItems

    const options = {
      height: 150,
      movableRows: true,
      movableColumns: true,
    }

    console.log(this.props.userItems)
    console.log(data)
    return (
      <div>
        <ReactTabulator 
          onRef={(r) => (this.ref = r)}
          columns={this.columns}
          data={data} 
          events={{ rowClick: this.rowClick }}
        />
        <i>
          Selected Title: <strong>{this.state.selectedTitle}</strong>
        </i>
      </div>
    )
  }
}

const mapStateToProps = ({ query: { userItems }}:any) => ({
  userItems,
})

export default connect(mapStateToProps, )(QueryTableTabulator)
