import React from 'react'

export class Time extends React.Component {
  state = {
    response: '',
    post: '',
    responseToPost: '',
  }

  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err))
  }
  /*
  callApi = async () => {
    const response = await fetch('/server/api/time')
    const body = await response.json()
    if (response.status !== 200) throw Error(body.message)
    return body
  }*/

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/server/api/data', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ post: this.state.post }),
    })
    const body = await response.text()

    this.setState({ responseToPost: body })
  }

  render() { console.log("component: time")
    return (
      <div className="Time">
        <p>{this.state.response}</p>
        <form onSubmit={this.handleSubmit}>
          <p><strong>Post to Server:</strong></p>
          <input type="text" value={this.state.post} onChange={e => this.setState({ post: e.target.value })} />
          <button type="submit">Submit</button>
        </form>
       <p style={{color : 'blue'}}><b>{this.state.responseToPost}</b></p>
      </div>
    )
  }
}

export default Time
